function heal () {
    if (potions > 0) {
        healAmount = randint(20, 30)
        playerHP += healAmount
        if (playerHP > 100) {
            playerHP = 100
        }
        playerBar.value = playerHP
        potions += -1
        game.splash("Healed " + healAmount + " HP!")
        enemyTurn()
    } else {
        game.splash("No potions left!")
    }
}
