function enemyTurn () {
    if (enemyHP > 0) {
        damage3 = randint(8, 15)
        playerHP += 0 - damage3
        playerBar.value = playerHP
        game.splash("Enemy hits for " + damage3 + "!")
    }
    checkGameOver()
}
